/**
  * @brief  Main program
  * @param  None
  * @retval None
  */
int main(void)
{
  uint32_t process_ongoing = 1;     /* HMAC processing on-going flag                          */
  uint32_t suspension_trigger = 1;  /* Low block processing suspension trigger                */
  uint32_t iteration_counter = 0;   /* Iteration counter for multi-buffer HMAC processing     */
  uint32_t block_length = 0;        /* Contains block length for multi-buffer HMAC processing */
  uint32_t step = 0;                /* Multi-buffer HMAC processing step counter              */
  uint8_t * input_data = NULL;      /* Pointer at input data                                  */
  
  
  /* STM32L4xx HAL library initialization:
       - Configure the Flash prefetch
       - Systick timer is configured by default as source of time base, but user 
         can eventually implement his proper time base source (a general purpose 
         timer for example or other time source), keeping in mind that Time base 
         duration should be kept 1ms since PPP_TIMEOUT_VALUEs are defined and 
         handled in milliseconds basis.
       - Set NVIC Group Priority to 4
       - Low Level Initialization
     */
  HAL_Init();

  /* Configure LED1 and LED3 */
  BSP_LED_Init(LED1);
  BSP_LED_Init(LED3);

  /* Configure the system clock to 120 MHz */
  SystemClock_Config();

  /****************************************************************************/
  /*                                                                          */
  /*       DMA-based HMAC SHA-224 processing suspension/resumption            */
  /*                                                                          */  
  /****************************************************************************/
  
  if (HAL_HASH_DeInit(&HashHandle) != HAL_OK)
  {
    Error_Handler();
  }  
  HashHandle.Init.DataType = HASH_DATATYPE_8B;
  HashHandle.Init.pKey = aKey_Large;
  HashHandle.Init.KeySize = LARGE_KEY_TAB_SIZE;    

  if (HAL_HASH_Init(&HashHandle) != HAL_OK)
  {
    Error_Handler();
  }
  
  process_ongoing = 1;      /* Flag indicating a block processing is on-going     */
  iteration_counter = 1;    /* Number of iterations (number of blocks processed ) */
  block_length = 1200;      /* Process the input data as a sequence of smaller blocks
                               each of size 1200 bytes, (except the last one that
                               may be smaller). 
                               This is used only to demonstrate the multi-buffer
                               HMAC suspension mechanism. */
  step = HMAC_FIRST_BUFFER; /* Step variable is set to process the first block */                            

  input_data = aInput_LowPrio_Large;  /* input_data points at the beginning of the input buffer */

  /********************************************************************/
  /*                                                                  */
  /* Start multi-buffer HMAC SHA-224 processing of low priority block */
  /*                                                                  */ 
  /********************************************************************/ 
  do
  {
    /* For each step, at first call or to resume processing after suspension, the API is invoked with
       the same handle and same parameters */           
    if ((*HMAC_SHA224_MultiBuffer_Functions[step])(&HashHandle, input_data, block_length) != HAL_OK)  
    {
      Error_Handler();
    }
    
    /**********************************************************************/    
    /* While the HASH process in on-going, a suspension request occurs
      (it may be triggered by an interruption in user code). In this example, 
      the suspension request is triggered by the flag suspension_trigger  */
    /**********************************************************************/       
    if (suspension_trigger == 1)
    {
      suspension_trigger = 0;  /* In this example, for demonstration purposes, each 
                                  block processing is suspended only once */
                                  
      /* Request DMA-based low priority block processing suspension */
      if (HAL_HASH_DMAFeed_ProcessSuspend(&HashHandle) != HAL_OK)
      {
          Error_Handler();
      }      
    }
    
    /*************************************************************************/
    /* Wait for low-priority block processing to stop, either upon a process 
       suspension or when the low-priority block processing is over          */
    /*************************************************************************/    
    while ((HAL_HASH_GetState(&HashHandle) != HAL_HASH_STATE_SUSPENDED)
           && (HAL_HASH_GetState(&HashHandle) != HAL_HASH_STATE_READY)); 
           
    /**********************************************************/           
    /* Check the low-priority block processing stop condition */
    /**********************************************************/          
    if (HAL_HASH_GetState(&HashHandle) == HAL_HASH_STATE_READY)
    {
      /* The block processing is over, move to next block. */

      switch (step)
      {
        case HMAC_FIRST_BUFFER:
          /* First block has been processed, move to the next */
          step = HMAC_INTERMEDIATE_BUFFER;
          /* Fall through ... */
          
        case HMAC_INTERMEDIATE_BUFFER:  
          
          input_data += block_length;  /* Make input_data point at the next block to process */
          iteration_counter++;         /* Increment the iteration (number of processed blocks) counter */ 
          
          /* Check whether the last block to process has been reached */
          if (block_length * iteration_counter >= LOW_PRIO_LARGE_INPUT_TAB_SIZE)  
          {
             /* The last block has been reached, update step */
             step = HMAC_LAST_BUFFER;
             block_length -=  (block_length * iteration_counter - LOW_PRIO_LARGE_INPUT_TAB_SIZE);
          } 
          
          /* For demonstration purposes, allow the next (low-priority) block 
             processing to be suspended */
          suspension_trigger = 1;
          break; 
          
        case HMAC_LAST_BUFFER:
          /* The last buffer has been processed. Mark the end of processing. */
          process_ongoing = 0;
          /* Update step to carry out digest retrieval next */
          step =  HMAC_DIGEST_RETRIEVAL;
          break;
          
        default:
          Error_Handler();        
          break;
      }
    } 
    else if (HAL_HASH_GetState(&HashHandle) == HAL_HASH_STATE_SUSPENDED)
    {
      /* The low-priority block processing is suspended, initiate
         the high-priority block processing */
      HigherPriorityBlock_Management(); 
      /* Next, low-priority block processing will be resumed in the do{} while() loop
        in re-invoking (*HMAC_SHA224_MultiBuffer_Functions[step])() */
    } 
    else
    {
      /* State is neither READY nor SUSPENDED: an error has occurred */
      Error_Handler();
    }            
  
  /******************************************************************************/ 
  /* Exit the do{} while() loop when the low-priority data full processing is over */ 
  /******************************************************************************/    
  } while (process_ongoing);       
  

  /*******************/ 
  /* Read the digest */ 
  /*******************/  
  if ((*HMAC_SHA224_MultiBuffer_Functions[step])(&HashHandle, aSHA224Digest, HASHTimeout) != HAL_OK)
  {
    Error_Handler();
  }
    
  if(memcmp(aSHA224Digest, aExpectSHA224Digest, SHA224_DIGEST_LENGTH) != 0)
  {
    Error_Handler();
  }  
  else
  {
    BSP_LED_On(LED1);  
  }   

  
 
  while (1)
  {
  }
}

/**
  * @brief  System Clock Configuration
  *         The system Clock is configured as follows :
  *            System Clock source            = PLL (MSI)
  *            SYSCLK(Hz)                     = 120000000
  *            HCLK(Hz)                       = 120000000
  *            AHB Prescaler                  = 1
  *            APB1 Prescaler                 = 1
  *            APB2 Prescaler                 = 1
  *            MSI Frequency(Hz)              = 4000000
  *            PLL_M                          = 1
  *            PLL_N                          = 60
  *            PLL_Q                          = 2
  *            PLL_R                          = 2
  *            PLL_P                          = 7
  *            Flash Latency(WS)              = 5
  * @param  None
  * @retval None
  */
void SystemClock_Config(void)
{
  RCC_ClkInitTypeDef RCC_ClkInitStruct = {0};
  RCC_OscInitTypeDef RCC_OscInitStruct = {0};
  
  /* Enable voltage range 1 boost mode for frequency above 80 Mhz */
  __HAL_RCC_PWR_CLK_ENABLE();
  HAL_PWREx_ControlVoltageScaling(PWR_REGULATOR_VOLTAGE_SCALE1_BOOST);
  __HAL_RCC_PWR_CLK_DISABLE();

  /* Enable MSI Oscillator and activate PLL with MSI as source */
  RCC_OscInitStruct.OscillatorType = RCC_OSCILLATORTYPE_MSI;
  RCC_OscInitStruct.MSIState = RCC_MSI_ON;
  RCC_OscInitStruct.MSICalibrationValue = RCC_MSICALIBRATION_DEFAULT;
  RCC_OscInitStruct.PLL.PLLState = RCC_PLL_ON;
  RCC_OscInitStruct.PLL.PLLSource = RCC_PLLSOURCE_MSI;
  RCC_OscInitStruct.MSIClockRange = RCC_MSIRANGE_6;
  RCC_OscInitStruct.PLL.PLLM = 1;
  RCC_OscInitStruct.PLL.PLLN = 60;
  RCC_OscInitStruct.PLL.PLLR = 2;
  RCC_OscInitStruct.PLL.PLLQ = 2;
  RCC_OscInitStruct.PLL.PLLP = 7;
  if (HAL_RCC_OscConfig(&RCC_OscInitStruct) != HAL_OK)
  {
    /* Initialization Error */
    while(1);
  }
  
  /* To avoid undershoot due to maximum frequency, select PLL as system clock source */
  /* with AHB prescaler divider 2 as first step */
  RCC_ClkInitStruct.ClockType = (RCC_CLOCKTYPE_SYSCLK | RCC_CLOCKTYPE_HCLK | RCC_CLOCKTYPE_PCLK1 | RCC_CLOCKTYPE_PCLK2);
  RCC_ClkInitStruct.SYSCLKSource = RCC_SYSCLKSOURCE_PLLCLK;
  RCC_ClkInitStruct.AHBCLKDivider = RCC_SYSCLK_DIV2;
  RCC_ClkInitStruct.APB1CLKDivider = RCC_HCLK_DIV1;  
  RCC_ClkInitStruct.APB2CLKDivider = RCC_HCLK_DIV1;  
  if(HAL_RCC_ClockConfig(&RCC_ClkInitStruct, FLASH_LATENCY_3) != HAL_OK)
  {
    /* Initialization Error */
    while(1);
  }

  /* AHB prescaler divider at 1 as second step */
  RCC_ClkInitStruct.ClockType = RCC_CLOCKTYPE_HCLK;
  RCC_ClkInitStruct.AHBCLKDivider = RCC_SYSCLK_DIV1;
  if(HAL_RCC_ClockConfig(&RCC_ClkInitStruct, FLASH_LATENCY_5) != HAL_OK)
  {
    /* Initialization Error */
    while(1);
  }
}

